import React from "react";
import {Image, View} from "@nodegui/react-nodegui";
import {winSize} from "./config";

const splashPng = ""

const splashSize = {
    width: 150,
    height: 150,
}
const splashPos = {
    x: (winSize.width - splashSize.width) / 2,
    y: (winSize.height - splashSize.height) / 2
}

export const Splash = () => {
    return (
        <View id="container" styleSheet={styleSheet}>
            <Image id="img"
                   geometry={{...splashSize, ...splashPos}}
                   buffer={Buffer.from(splashPng, "base64")}
            />
        </View>
    )
}

const styleSheet = `
#container {
    width: ${winSize.width}px;
    height: ${winSize.height}px;
}
#img {
    width: ${splashSize.width}px;
    height: ${splashSize.height}px;
    
    left: ${splashPos.x}px;
    top: ${splashPos.y}px;
}
`
